docker exec -i teachbaseai-postgres-1 psql -U teachbaseai -d teachbaseai -c "select created_at, trace_id, portal_id, domain, (hints_json->>'event_name') as event_name, (headers_json->>'user-agent') as user_agent, (headers_json->>'x-forwarded-for') as xff, length(body_preview) as body_preview_len, body_sha256, status_hint from bitrix_inbound_events order by created_at desc limit 50;"
