docker exec -i teachbaseai-postgres-1 psql -U teachbaseai -d teachbaseai -c "select portal_id, (access_token is not null) as has_access_token, (refresh_token is not null) as has_refresh_token, length(access_token) as access_len, length(refresh_token) as refresh_len, md5(coalesce(access_token,'')) as access_md5, md5(coalesce(refresh_token,'')) as refresh_md5, expires_at, updated_at from portal_tokens where portal_id=2;"
