docker exec -i teachbaseai-postgres-1 psql -U teachbaseai -d teachbaseai -c "select created_at, portal_id, kind, status_code, (summary_json::jsonb)->>'bitrix_error_code' as error_code, (summary_json::jsonb)->>'bitrix_error_desc' as error_desc, (summary_json::jsonb)->>'credentials_source' as credentials_source from bitrix_http_logs where trace_id in ('971485c4-826a-40','b85f1def-baa3-41','b97203eb-638b-41') order by created_at;"
