cd /opt/teachbaseai
docker compose -f docker-compose.prod.yml exec -T backend python -c 'from redis import Redis; from rq import Queue, Worker; from apps.backend.config import get_settings; s=get_settings(); r=Redis(host=s.redis_host, port=s.redis_port); qi=Queue(s.rq_ingest_queue_name, connection=r); qo=Queue(s.rq_outbox_queue_name, connection=r); print({"ingest_queue":qi.count,"ingest_started":qi.started_job_registry.count,"outbox_queue":qo.count,"outbox_started":qo.started_job_registry.count,"failed":qi.failed_job_registry.count}); print("workers", [w.name for w in Worker.all(connection=r)])'
