docker exec teachbaseai-backend-1 python -c "import json; from apps.backend.database import get_session_factory; from apps.backend.services.kb_settings import get_portal_kb_settings,get_bot_settings,get_effective_gigachat_settings; Session=get_session_factory(); db=Session(); pid=2; print('PORTAL',pid); print('portal_kb_settings='); print(json.dumps(get_portal_kb_settings(db,pid),ensure_ascii=False,indent=2)); print('bot_settings='); print(json.dumps(get_bot_settings(db),ensure_ascii=False,indent=2)); print('effective='); print(json.dumps(get_effective_gigachat_settings(db,pid),ensure_ascii=False,indent=2)); db.close()"
